            ['name' => 'dashboard-customer-report',     'description' => 'User can see dashboard customer report', 'is_business' => 1, 'group' => 'dashboard'],
        ];
        foreach ($permissions as $permission) {
            $exist = PermissionModel::where(['name' => $permission['name'], 'is_business' => $permission['is_business'], 'group' => $permission['group']])->first();
            if (!$exist) {
                PermissionModel::create(['name' => $permission['name'], 'description' => $permission['description'], 'group' => $permission['group'], 'is_business' => $permission['is_business']]);
            }else{
                $exist->description = $permission['description'];
                $exist->save();
            }

        }
    }
}
